    const [helloVisible, setHelloVisible] = useState(true);

    useEffect(() => {
        setTimeout(() => {
            setHelloVisible(false);
        }, 1500)
    }, []);

    return <div className={styles.main}>
        {helloVisible && <motion.h1 className={`${styles.hello} ${styles.mover}`}>hEllo</motion.h1>}
        {!helloVisible && <motion.h1 
        initial={{x: -40}}
        animate={{x: 0, scale: 2}}
