        //     questions.push(...questions);
        //     const updatedData = JSON.stringify(questions, null, 2);
        //     fs.writeFile('data.json', updatedData, 'utf8', (err) => {
        //       if (err) return;
        //     });
        //   });
        // var exisitingQuestions = JSON.parse(questions);
        // exisitingQuestions.push(...questions);
        // if(res.question.trim() !== '') exisitingQuestions.push(res);
        // console.log(questions);
        // console.log(exisitingQuestions);

    }

    return <div className={styles.main}>
        <div className={styles.bar}>
            <div>{warning}</div>
            <button onClick={addQuestionHandler}>Add Question</button>
        </div>

        {Array(questions.length + 1).fill("").map((_, idx) => < PostQuestionForm id={idx + 1} key={idx} ref={questionRef}/>)}

        <button onClick={submitQuestionsHandler}>post</button>
    </div >
}

