import PostQuestionForm from "./PostQuestionForm";
import styles from "./PostQuestions.module.scss";
import { useState } from "react";
import { useRef } from "react";

const ques = {
    id: 1,
    question: "",
    rightChoices: [],
    wrongChoices: []
}

const PostQuestions = () => {

    const [noOfQuestions, setNoOfQuestions] = useState(1);
    const [questions, setQuestions] = useState([]);
    const questionRef = useRef(null);

    const addQuestionHandler = () => {
        setNoOfQuestions(prev => prev + 1);
        const question = Array.from(questionRef.current.children).filter(child => child.tagName === "INPUT")[0].value;
        const choicesElement = Array.from(questionRef.current.children).filter(child => child.tagName === "DIV")[0];
        const rightOptions = Array.from(choicesElement.children[0].querySelectorAll('input')).map(i => i.value);
        const wrongOptions = Array.from(choicesElement.children[1].querySelectorAll('input')).map(i => i.value);

        setQuestions(prev => [...prev, {id: prev.length + 1, question, rightOptions, wrongOptions}])
    }

    return <div className={styles.main}>
        <div className={styles.bar}>
            <div>Post Questions</div>
            <button onClick={addQuestionHandler}>Add Question</button>
        </div>

        {Array(noOfQuestions).fill("").map((a, idx) => < PostQuestionForm id={idx} ref={questionRef}/>)}

        <button>post</button>
    </div >
}

export default PostQuestions;
