import styles from "./Choices.module.scss";
import { useState } from "react";

const Choices = ({ choices, identifier }) => {

    const [output, setOutput] = useState(choices);

    const addChoiceHandler = () => {
        setOutput(prev => [...prev, {"id": prev[prev.length - 1].id + 1, "choice": ""}]);
    }

    const removeInputHandler = (idx) => {
        setOutput(prev => prev.filter((a, indx) => indx !== idx));
    }

    return (<div className={styles.main}>
        {/* <h1>{name}</h1> */}
        {output.map((a, idx) => <div className={styles.choice}>
            <input key={idx} type="text" placeholder={identifier + " " + a.id} />
            <div onClick={() => removeInputHandler(idx)}><span class="material-symbols-outlined">
                cancel
            </span></div>
        </div>)
        }
        <div className={styles.add} onClick={addChoiceHandler} >add a choice</div>
    </div >)
}

export default Choices;
