import styles from "./Choices.module.scss";
import { useState } from "react";

const Choices = ({ defaultChoices }) => {
    const [noOfChoices, setNoOfChoices] = useState(+defaultChoices);

    const setNoOfChoicesHandler = () => {
        setNoOfChoices(prev => prev + 1);
    }

    return (<div className={styles.main}>

        {Array(noOfChoices).fill(0).map((a, idx) => <div className={styles.choice}>
            <input key={idx} type=" text" />
            <div><span class="material-symbols-outlined">
                cancel
            </span></div>
        </div>)
        }
        < div onClick={setNoOfChoicesHandler} > add a choice</div>
    </div >)
}

export default Choices;
