import styles from "./Choices.module.scss";
import { forwardRef, useState } from "react";

const Choices = forwardRef(({ identifier }, ref) => {

    const addChoiceHandler = () => {
        setOutput(prev => [...prev, ""]);
        setNoOfChoices(prev => prev + 1);
    }

    const removeInputHandler = (idx) => {

        Array.from(ref.current.children).forEach((a, i) =>  {
            console.log(a);
            if (i === idx) a.remove();
        });
    }

    return (<div className={styles.main}>
        <div ref={ref}>
            {Array(noOfChoices).fill("").map((a, idx) => <div className={styles.choice}>
                <input key={idx} type="text" placeholder={identifier + " " + a.id} />
                <div onClick={() => removeInputHandler(idx)}><span className="material-symbols-outlined">
                    cancel
                </span></div>
            </div>)
            }
        </div>

        <div className={styles.add} onClick={addChoiceHandler} >
            <span className="material-symbols-outlined">
                add_circle
            </span>
        </div>
    </div >)
})

export default Choices;
