import styles from "./PostQuestionForm.module.scss";
import Choices from "./Choices";
import { forwardRef, useRef } from "react";

const PostQuestionForm = forwardRef(({ id }, ref) => {
    const correctChoicesRef = useRef(null);
    const wrongChoicesRef = useRef(null);

    const submitHandler = e => {
        e.preventDefault();
        var rightChoicesInputs = [];
        var wrongChoicesInputs = [];
        correctChoicesRef.current.querySelectorAll('input').forEach(choice => rightChoicesInputs.push(choice.value));
        wrongChoicesRef.current.querySelectorAll('input').forEach(choice => wrongChoicesInputs.push(choice.value));
    }

    return (<form onSubmit={submitHandler} className={styles.main} >
        <label htmlFor={"question" + id}>Question {id} </label>
        <input type="text" id={"question" + id} ref={questionRef}/>
        <div className={styles.choices}>
            <Choices ref={correctChoicesRef} identifier="right choice" />
            <Choices ref={wrongChoicesRef} identifier="wrong choice" />
        </div>
        <button type="submit">get</button>
    </form >)
})

export default PostQuestionForm;
