import styles from "./PostQuestionForm.module.scss";
import Choices from "./Choices";

const PostQuestionForm = ({ question }) => {
    const submitHandler = e => {
        e.preventDefault();
        console.log("submitting");
    }

    return (<form onSubmit={submitHandler} className={styles.main}>
        <label htmlFor={"question" + question.id}>Question {question.id} </label>
        <input type="text" id={"question" + question.id} />
        <div className={styles.choices}>
            <Choices choices={question.rightChoices} ide/>
            <Choices choices={question.wrongChoices}/>
        </div>
        {/* <button type="submit">submit</button> */}
    </form >)
}

export default PostQuestionForm;
