import styles from "./Choices.module.scss";
import { forwardRef, useState } from "react";

const Choices = forwardRef(({ identifier }, ref) => {

    const [choiceIds, setChoiceIds] = useState([Math.flooMath.random()*100000]);

    const removeInputHandler = (a) => {
        Array.from(ref.current.children).forEach(a =>  {
            console.log(a.firstChild.getAttribute('placeholder'));
            // if (i === idx) a.remove();
        });
    }

    const addChoiceHandler = () => {
        console.log(choiceIds);

        setChoiceIds(prev => [...prev, Math.random()*100000]);
    }

    return (<div className={styles.main}>
        <div ref={ref}>
            {choiceIds.map(a => <div className={styles.choice}>
                <input key={a} type="text" placeholder={identifier + " " + a} />
                <div onClick={() => removeInputHandler(a)}><span className="material-symbols-outlined">
                    cancel
                </span></div>
            </div>)
            }
        </div>

        <div className={styles.add} onClick={addChoiceHandler} >
            <span className="material-symbols-outlined">
                add_circle
            </span>
        </div>
    </div >)
})

export default Choices;
