import styles from "./Choices.module.scss";
import { forwardRef, useState } from "react";

const Choices = forwardRef(({ choices, identifier }, ref) => {

    const [output, setOutput] = useState(choices);

    const addChoiceHandler = () => {
        setOutput(prev => [...prev, { "id": prev[prev.length - 1]?.id + 1 || 1, "choice": "" }]);
    }

    const removeInputHandler = (idx) => {
        setOutput(prev => prev.filter((_, indx) => indx !== idx));
    }

    return (<div className={styles.main} ref={ref}>
        <div>

        </div>

        <div className={styles.add} onClick={addChoiceHandler} >
            <span className="material-symbols-outlined">
                add_circle
            </span>
        </div>
    </div >)
})

export default Choices;
