import styles from "./Questions.module.scss";
import questions from "../questions.json";
import Question from "./Quesion";
import { useRef } from "react";


const Questions = () => {

    const questionsRef = useRef(null);

    const submitHandler = () => {
        var marks = []
        const results = Array.from(questionsRef.current.children).filter(child => child.className.includes('main'));
        const resultsAns = results.map(result => result.children[0]);

        for(var i=0; i<results.length; i++){
            const attemptedCorrectChoices = results[i].rightChoices;
            const expectedCorrectChoices = questions[i].rightChoices;

            if(attemptedCorrectChoices.length != expectedCorrectChoices.length) marks[results[i].question] = 0;

        }
    }


    return <div className={styles.main} ref={questionsRef}>
        {questions.map(question => <Question key={question.question} question={question.question} rightChoices={question.rightChoices} wrongChoices={question.wrongChoices}/>)}
        <button onClick={submitHandler}>submit</button>
    </div>
}

export default Questions;
